import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import "./singlePost.css"

export default function SinglePost() {
    const location = useLocation();

    //variable that stores postID
    const path = (location.pathname.split("/")[2]);

    const [post, setPost] = useState({})

    useEffect(()=>{
        const getPost = async ()=>{
            const res = await axios.get("/posts/"+path);
            setPost(res.data)
        }
        getPost()
    }, [path])
    return (
        <div className="singlePost">
            <div className="singlePostWrapper">
            <h1 className="singlePostTitle">{post.title}
                <div className="singlePostEdit">
                <i className="singlePostIcon far fa-edit"></i>
                <i className="singlePostIcon far fa-trash-alt"></i>
                </div>
                </h1>
                {post.photo && (<img src={post.photo} alt="postImg" className="singlePostImg"/>)}

                <div className="singlePostInfo">
                    <span className="singlePostAuthor">Posted By: <b>{post.username}</b></span>
                    <span className="singlePostDate">1 Hour Ago</span>
                </div>
                <div className = "workoutContainer">
                    <div className="exerciseCol">
                        <b>Exercise</b><br/>
                        {post.firstExercise}</div>
                    <div className="weightCol"><b>Weight</b><br/>{post.firstExerciseWeight}</div>
                    <div className="setCol"><b>Sets</b><br/>{post.firstExerciseSets}</div>
                    <div className="repCol"><b>Reps</b><br/>{post.firstExerciseReps}</div>
                </div>
                <div className = "workoutContainer">
                    {post.secondExercise && (<div className="exerciseCol">
                        <b>Exercise</b><br/>
                        {post.secondExercise}</div>)}
                    {post.secondExerciseWeight && (<div className="weightCol"><b>Weight</b><br/>{post.secondExerciseWeight}</div>)}
                    <div className="setCol"><b>Sets</b><br/>{post.firstExerciseSets}</div>
                    <div className="repCol"><b>Reps</b><br/>{post.firstExerciseReps}</div>
                </div><div className = "workoutContainer">
                    <div className="exerciseCol">
                        <b>Exercise</b><br/>
                        {post.firstExercise}</div>
                    <div className="weightCol"><b>Weight</b><br/>{post.firstExerciseWeight}</div>
                    <div className="setCol"><b>Sets</b><br/>{post.firstExerciseSets}</div>
                    <div className="repCol"><b>Reps</b><br/>{post.firstExerciseReps}</div>
                </div>

                <p className="singlePostDescription">{post.description}</p>
            </div>
        </div>
    )
}
